import {getAccessToken} from "../../core/api/get-access-token.api"

export const tokenReducer: any = (state, action) => {
  switch (action.type) {
    case "login":
      return {accessToken:action.payload.accessToken,refereshToken:action.payload.refereshToken} 
    case "accessTokenExpired":
      return getAccessToken(state.refe).then(res => {

      })
      return {...state, accessToken:null }
    case "exit":
      return {accessToken:null,refereshToken:null}
    default:
      return state
  }
}
