export const tokenReducer: any = (state, action) => {
  switch (action.type) {
    case "login":
      return {accessToken:action.payload.accessToken,refreshToken:action.payload.refereshToken} 
    case "accessTokenExpired":
      return {...state, accessToken:null }
    case "exit":
      return {accessToken:null,refreshToken:null}
    default:
      return state
  }
}
